mapper.select:
        SELECT


--         级联表的字段

        FROM changdi  a



                and a.id in

                #{item}



                and a.changdi_uuid_number like CONCAT('%',#{params.changdiUuidNumber},'%')


                and a.changdi_name like CONCAT('%',#{params.changdiName},'%')


                and a.changdi_types = #{params.changdiTypes}


                  and a.changdi_old_money >= #{params.changdiOldMoneyStart}   


                  and a.changdi_old_money <= #{params.changdiOldMoneyEnd}   


                  and a.changdi_new_money >= #{params.changdiNewMoneyStart}   


                  and a.changdi_new_money <= #{params.changdiNewMoneyEnd}   


                and a.shijianduan like CONCAT('%',#{params.shijianduan},'%')


                  and a.shijianduan_ren >= #{params.shijianduanRenStart}   


                  and a.shijianduan_ren <= #{params.shijianduanRenEnd}   


                and a.shijianduan_ren = #{params.shijianduanRen}


                  and a.changdi_clicknum >= #{params.changdiClicknumStart}   


                  and a.changdi_clicknum <= #{params.changdiClicknumEnd}   


                and a.changdi_clicknum = #{params.changdiClicknum}


                and a.banquan_types = #{params.banquanTypes}


                and a.shangxia_types = #{params.shangxiaTypes}


                and a.tuijian like CONCAT('%',#{params.tuijian},'%')


                  and a.changdi_delete >= #{params.changdiDeleteStart}   


                  and a.changdi_delete <= #{params.changdiDeleteEnd}   


                and a.changdi_delete = #{params.changdiDelete}


                and a.changdi_content like CONCAT('%',#{params.changdiContent},'%')




        order by a.${params.orderBy} desc
